import React, { useState } from "react";
import FetchWeather, { fetchWeather } from "./api/fetchData";
import "./App.module.css";
const App = () => {
  const [query, setQuery] = useState("");
  const search = async (e) => {
    if (e.key === "Enter") {
      const data = await fetchWeather(query);
      alert(data);
    }
  };
  return (
    <React.Fragment>
      <div className="main-container">
        <input
          type="text"
          className="search"
          placeholder="Search...."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyPress={search}
        />
      </div>
    </React.Fragment>
  );
};

export default App;
