import React, { useState } from "react";
import FetchWeather from "./api/fetchData";
import "./App.module.css";
const App = () => {
  const [query, setQuery] = useState("");
  return (
    <React.Fragment>
      <div className="main-container">
        <input
          type="text"
          className="search"
          placeholder="Search...."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>
    </React.Fragment>
  );
};

export default App;
